mapper.update:
	UPDATE MEDICAL_DEVICE
	   SET DEVICE_NAME = #{ deviceName }
	     , DEVICE_MODEL = #{ deviceModel }
		 , DEVICE_CONTENT = #{ deviceContent }
		 , MODIFY_DATE = SYSDATE
		 , CATEGORY_CODE = #{ categoryCode }
     WHERE DEVICE_NO = #{ deviceNo }

UPDATE DEVICE_FILE
   SET DCHANGE_NAME = #{ dchangeName }
     , DFILE_PATH = #{ dfilePath }
 WHERE DEVICE_NO = #{ deviceNo }

UPDATE MEDICAL_DEVICE
   SET STATUS = 'N'
 WHERE DEVICE_NO = #{ deviceNo }

mapper.delete:
DELETE 
  FROM DEVICE_FILE
 WHERE DEVICE_NO = #{ deviceNo }
