mapper.insert:
UPDATE QUESTION
   SET Q_HIT = Q_HIT + 1
 WHERE Q_NO = #{ qno }

INSERT
	INTO QUESTION
	(
		Q_NO
	  , Q_TITLE
	  , CREATE_DATE
	  , Q_CONTENT
	  , STATUS
	  , SECRET_STATUS
	  , USER_NO
	  , CATEGORY_NO	
	)
	VALUES
	(
	  	SEQ_Q_NO.NEXTVAL
	  , #{ qtitle }
	  , SYSDATE
	  , #{ qcontent }
	  , 'N'
	  , #{ secretStatus }
	  , #{ userNo }
	  , #{ categoryNo } 
	)

mapper.update:
UPDATE QUESTION
   SET Q_TITLE = #{ qtitle }
     , MODIFY_DATE = SYSDATE
     , Q_CONTENT = #{ qcontent }
     , SECRET_STATUS = #{ secretStatus }
     , CATEGORY_NO = #{ categoryNo }
    WHERE Q_NO = #{ qno }

mapper.delete:
UPDATE QUESTION
   SET STATUS = 'Y'
 WHERE Q_NO = #{ qno }

mapper.update:
UPDATE ANSWER
   SET A_CONTENT = #{ acontent }
     , A_MODIFY_DATE = SYSDATE
 WHERE Q_NO = #{ qno }

mapper.delete:
UPDATE ANSWER
   SET STATUS = 'Y'
 WHERE Q_NO = #{ qno }
