mapper.select:
		SELECT
           	   RESERVATION_TIME
	  	  FROM RESERVATION_INFORMATION   
         WHERE VNO = #{ vno }
           AND RESERVATION_DATE = #{ reservation_date }
           AND RESERVATION_STATUS = '예약완료'

mapper.insert:

	SELECT USER_NO
	  FROM MEMBER
	 WHERE USER_ID = #{id}

INSERT
  INTO RESERVATION_INFORMATION
  (
  	   RESERVATION_NO
  	 , RESERVATION_DATE
  	 , RESERVATION_TIME
  	 , RESERVATION_STATUS
  	 , SYMPTOM
  	 , TNO
  	 , USER_NO
  	 , VNO
  )
  VALUES
  (
  	    SEQ_RESERVATION_NO.NEXTVAL
  	  , #{ reservationInfo.reservation_date }
  	  , #{ reservationInfo.reservation_time }
  	  , DEFAULT
  	  , #{ reservationInfo.symptom }
  	  , #{ reservationInfo.tno }
  	  , #{ user_no }
  	  , #{ reservationInfo.vno }
  )

INSERT
  INTO DOG_INFORMATION_INPUT
  (
  	   RESERVATION_NO
  	 , IN_DOG_NAME
  	 , IN_BREED
  	 , IN_DOG_GENDER
  	 , IN_DOG_AGE
  )
  VALUES
  (
  	   SEQ_RESERVATION_NO.CURRVAL
   	 , #{ in_dog_name }
   	 , #{ in_breed }
   	 , #{ in_dog_gender }
   	 , #{ in_dog_age }
  )
