mapper.insert:
INSERT 
	INTO REVIEW
	(
		RV_NO
	  , RV_TITLE
	  , RV_CONTENT
	  , CREATE_DATE
	  , STATUS
	  , TNO
	  , RESERVATION_NO
	  , USER_NO
	)
	VALUES
	(
		SEQ_RV_NO.NEXTVAL
	  , #{ rvtitle }
	  , #{ rvcontent }
	  ,	SYSDATE
	  , DEFAULT
	  , #{ tno }
	  , #{ resNo }
	  , #{ userNo }
	)

UPDATE REVIEW
   SET RV_HIT = RV_HIT + 1
 WHERE RV_NO = #{ rvno }
