mapper.insert:
INSERT
  INTO VETERINARIAN
(
	VNO
  , VNAME
  , TNO
  , PROFILE
  , CAREER
  , STATUS
  , PHONE
)
VALUES
(
	SEQ_VNO.NEXTVAL
  , #{ vname, jdbcType=VARCHAR }
  , #{ tno }
  , #{ profile, jdbcType=VARCHAR }
  , #{ career, jdbcType=VARCHAR }
  , DEFAULT
  , #{ phone, jdbcType=VARCHAR }
)

mapper.update:
UPDATE VETERINARIAN
   SET PROFILE = #{ profile }
     , CAREER = #{ career }
     , PHONE = #{ phone }
 WHERE VNO = #{ vno }
